import React, { Component } from 'react';

import { AuthUserContext, withAuthorization } from '../Session';
import TradingViewWidget, { Themes } from 'react-tradingview-widget'
import { Alert, Table, Card, Col, Row, CardText, Button } from 'reactstrap';
//import {Button, Input, Label} from 'reactstrap';
import './home.css';
import 'react-autocomplete-input/dist/bundle.css'
import Autocomplete from "./Autocomplete"

var App = React.createClass({
  getInitialState(){
    return{
      donutval:55
    }
  },
  updateVal(e){
    this.setState({donutval:e.target.value})
  },
  render(){
    return(
      <div>
        <DonutChart value={this.state.donutval} />
        <br/>
        <label>Enter a value from 1-100<br/>
          <input onChange={this.updateVal} type="number" min="0" max="100" />
        </label>
      </div>
    )
  }
});


const DonutChart = React.createClass({
  propTypes: {
    value: React.PropTypes.number,        // value the chart should show
    valuelabel: React.PropTypes.string,   // label for the chart
    size: React.PropTypes.number,         // diameter of chart
    strokewidth: React.PropTypes.number   // width of chart line
  },
  getDefaultProps() {
    return {
      value:0,
      valuelabel:'Completed',
      size:116,
      strokewidth:26
    };
  },
  render() {

    const halfsize = (this.props.size * 0.5);
    const radius = halfsize - (this.props.strokewidth * 0.5);
    const circumference = 2 * Math.PI * radius;
    const strokeval = ((this.props.value * circumference) / 100);
    const dashval = (strokeval + ' ' + circumference);

    const trackstyle = {strokeWidth: this.props.strokewidth};
    const indicatorstyle = {strokeWidth: this.props.strokewidth, strokeDasharray: dashval}
    const rotateval = 'rotate(-90 '+halfsize+','+halfsize+')';

    return (
      <svg width={this.props.size} height={this.props.size} className="donutchart">
        <circle r={radius} cx={halfsize} cy={halfsize} transform={rotateval} style={trackstyle} className="donutchart-track"/>
        <circle r={radius} cx={halfsize} cy={halfsize} transform={rotateval} style={indicatorstyle} className="donutchart-indicator"/>
        <text className="donutchart-text" x={halfsize} y={halfsize} style={{textAnchor:'middle'}} >
          <tspan className="donutchart-text-val">{this.props.value}</tspan>
          <tspan className="donutchart-text-percent">%</tspan>
          <tspan className="donutchart-text-label" x={halfsize} y={halfsize+10}>{this.props.valuelabel}</tspan>
        </text>
      </svg>
    );
  }
})



React.render(<App/>, document.querySelector("#main"));


class HomePage extends Component {

  constructor(){
    super();
    this.state = {
      authUser: null,
      balance: '',

    }
    //this.handleChange = this.handleChange.bind(this);
    //this.handleSubmitStock = this.handleSubmitStock.bind(this);
  }

  toggleDarkLight = event => {
   var body = document.getElementById("body");
   var currentClass = body.className;
   body.className = currentClass == "dark-mode" ? "light-mode" : "dark-mode";
 }

  async componentDidMount() {
    this.props.firebase.db.collection("Users").doc(this.props.firebase.auth.O).onSnapshot(docSnapshot => {
      console.log(`Received doc snapshot: docSnapshot`, docSnapshot.data());
      this.setState({
        balance: docSnapshot.data().balance,
        username: docSnapshot.data().username
      });      // ...
    }, err => {
      console.log(`Encountered error: ${err}`);
    });
    var db = this.props.firebase.db;
    //get collection for stocks per user

    this.favs = db.collection("Users").doc(this.props.firebase.auth.O).collection("favorites").onSnapshot(querySnapshot=> {
      querySnapshot.forEach(element => {
        this.renderFavorites(element, element.id);
    });
    })


    this.leaders = db.collection("Users").orderBy('accuracy', 'desc').limit(5).onSnapshot(querySnapshot=> {
      document.getElementById("leaders").innerHTML = "";
      querySnapshot.forEach(element => {
        this.renderLeaderBoard(element, element.id);
      });
    })

  }

  componentWillUnmount() {
    this.favs();
    this.leaders();
  }


  renderLeaderBoard(leader, id){
    var row = document.createElement('tr');
    var leaderTD = document.createElement('td');
    leaderTD.textContent = leader.data().username;
    var accuracyTD = document.createElement('td');
    accuracyTD.textContent = leader.data().accuracy;

    row.appendChild(leaderTD);
    row.appendChild(accuracyTD);
    document.getElementById("leaders").appendChild(row);

  }

  renderFavorites(ticker, id){
    var row = document.createElement('tr');
    row.setAttribute('id', id);
    var ticker1 = document.createElement('td');
    ticker1.textContent = ticker.data().Ticker;
    var price = document.createElement('td');
    price.textContent = ticker.data().price;

    row.appendChild(ticker1);
    row.appendChild(price);
    document.getElementById("favs").appendChild(row);

  }

  render() {
    return (
      <AuthUserContext.Consumer>
      {authUser => (
      <div>
        <body id="body" class="light-mode">
        <Button color="primary" name="dark_light" onClick= {this.toggleDarkLight} title="Toggle dark/light mode">Change Theme</Button>
        <div className="home-page">
        <Row>
          <Col sm="4">
            <h1>Hi, {this.state.username}!</h1>
          </Col>
          <Col sm="4">
            <Alert color="primary">
              Token Balance: {this.state.balance}
            </Alert>
          </Col>
        </Row>
        </div>
        <Row>
        <Col sm="6">
          <h3>Favorites</h3>
                <Card body outline color="info">


                <CardText>
              <Table>
                <thead>
                  <tr>
                    <th>Ticker</th>
                    <th>Price</th>
                  </tr>
                </thead>
                <tbody id ="favs">

                </tbody>
              </Table>
            </CardText>
            </Card>
            </Col>

          <Col sm="6">

              <h3>Leaderboard</h3>
                <Card body outline color="info">
                  <CardText>
              <Table>
                <thead>
                  <tr>
                    <th>User</th>
                    <th>Score</th>
                  </tr>
                </thead>
                <tbody id="leaders">

                </tbody>
              </Table>
            </CardText>
            </Card>
          </Col>
          <Col sm="6">
            <h3>Popular Stocks</h3>
              <Card body outline color="info">
                <CardText>
            <Table>

              <thead>
        <tr>
          <th>Stocks Name</th>
        </tr>
      </thead>
      <tbody>
        <tr>
          <th>AAPL</th>
        </tr>
        <tr>
          <th>MSFT</th>
        </tr>
        <tr>
          <th>TWTR</th>
        </tr>
      </tbody>
            </Table>
            </CardText>
          </Card>
          </Col>
          <Col sm="12">
          <div className="stock-chart">
